// Minimal marked.js implementation (if you can't use CDN)
window.marked = function(markdown) {
    // Simple conversion for headings and lists
    return markdown
        .replace(/^# (.*$)/gm, '<h1>$1</h1>')
        .replace(/^## (.*$)/gm, '<h2>$1</h2>')
        .replace(/^- (.*$)/gm, '<li>$1</li>')
        .replace(/\n/g, '<br>');
};